import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component {

  renderSquare(i) {
      return (
        <Square
          value={this.props.squares[i]}
          onClick={() => this.props.onClick(i)}
        />
      );
    }

  render() {
    console.log(this.props.size);
    let boardsize = this.props.size;
    let cols = [];
    for(let i = 0; i < boardsize; i++) {
      let row = [];
      for(let j = 0; j< boardsize; j++) {
        row.push(this.renderSquare(i * boardsize + j));
      }
      cols.push(<div key={i} className="board-row">{row}</div>);
    }

    return (
    <div>{cols}</div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      boardsize: 3,
      stepNumber: 0,
      xIsNext: true,
      winner: false,
      history: [{
        squares:Array(9).fill(null),
      }],
    };
  }

  handleChange(event) {
    this.setState({boardsize: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(event.target.size.value);
    this.setState({winner: false});
    this.setState({boardsize: event.target.size.value});
    this.setState({stepNumber: 0});
    let boardLength = event.target.size.value * event.target.size.value;
    console.log(boardLength);
    console.log(typeof boardLength);
    this.setState({history: [{
      squares:Array(boardLength).fill(null),
    }],});
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      history: history.concat([{
        squares: squares,
        lastCoords: i
      }]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext,
    });
  }

  jumpTo(step) {
   this.setState({
     stepNumber: step,
     xIsNext: (step % 2) === 0,
   });
 }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    this.setState({winner: calculateWinner(current.squares, this.state.boardsize)});
    const moves = history.map((step, move) => {
      const lastCoords = step.lastCoords;
      const col = 1 + lastCoords % this.state.boardsize;
      const row = 1 + Math.floor(lastCoords / this.state.boardsize);
      const desc = move ?
        `Go to move #${move} (${col}, ${row})` :
        'Go to game start';
      return (
        <li key={move}>
          <button onClick={() => this.jumpTo(move)}>{desc}</button>
        </li>
      );
    });


    let status;
    if (this.state.winner) {
      status = 'Winner: ' + this.state.winner;
    } else {
     status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div className="game">
      <form className="sizeform" onSubmit={this.handleSubmit}>
        <label>
          Board Size:
          <input type="number"
            name="size"
            defaultValue={this.state.boardsize}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
        <div className="game-board">
          <Board
          squares={current.squares}
          size={this.state.boardsize}
          onClick={(i) => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{moves}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);

function calculateWinner(squares, boardsize) {

let boardlength = boardsize * boardsize;


let verticalwin = false;
let horizontalwin = false;
let diagonalwin = false;

//test for vertical win
for(let i = 0; i < boardsize; i++) {
  let verticalsquares = [];
  for(let j = i; j < boardlength; j += boardsize) {
    verticalsquares.push(squares[j]);
  }
  verticalwin = verticalsquares.every(v => v === verticalsquares[i]);
  if(verticalwin) return verticalsquares[0];
}
//test for horizontal victory
for(let i = 0; i < boardlength; i += boardsize) {
  let horizontalsquares = [];
  for(let j = 0; j < boardsize; j++) {
    horizontalsquares.push(squares[j]);
  }
  horizontalwin = horizontalsquares.every(h => h === horizontalsquares[i]);
  if(horizontalwin) return horizontalsquares[0];
}

return null;


/*
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];

    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
  */
}
